mapper.select:

select id, name, password, join_date as joinDate
from user
where id = #{id }


select id, name, password, join_date as joinDate
from user 
where id=#{id }
 and password=#{password }


select name, email, password, gender
from user
where no = #{no }

mapper.update:

update user
set name = #{name },  gender=#{gender }
where no = #{no }

mapper.delete:

delete
from guestbook
where no = #{no }
and password = #{password }
